static int cpu_flags_all_zero (struct cpu_flags *cpu_flags_p) {

    struct cpu_flags temp = {0};
    return !memcmp (cpu_flags_p, &temp, sizeof (temp));

}

static struct cpu_flags cpu_flags_or (struct cpu_flags a, struct cpu_flags b) {

    size_t i;
    
    for (i = 0; i < sizeof (a); i++) {
        ((unsigned char *) &a)[i] |= ((unsigned char *) &b)[i];
    }
    
    return a;

}

static struct cpu_flags cpu_flags_and (struct cpu_flags a, struct cpu_flags b) {

    size_t i;
    
    for (i = 0; i < sizeof (a); i++) {
        ((unsigned char *) &a)[i] &= ((unsigned char *) &b)[i];
    }
    
    return a;

}

static struct cpu_flags cpu_flags_and_not (struct cpu_flags a, struct cpu_flags b) {

    size_t i;
    
    for (i = 0; i < sizeof (a); i++) {
        ((unsigned char *) &a)[i] &= ~((unsigned char *) &b)[i];
    }
    
    return a;

}

static void machine_dependent_set_march (const char *optarg) {

    char *arg, *orig_arg;
    char *next;
    
    arg = orig_arg = xstrdup (optarg);
    
    if (cpu_extensions_name == 0) {
        cpu_extensions_name = xstrdup ("");
    }
    
    if (*arg == '+') {
        ++arg;
    }
    
    while (1) {
    
        unsigned long i;
        char saved_c;
        
        next = strchr (arg, '+');
        
        if (next == 0) {
            next = arg + strlen (arg);
        }
        
        saved_c = *next;
        *next = '\0';
        
        for (i = 0; i < ARRAY_SIZE (cpu_archs); i++) {
        
            if (strcmp (arg, cpu_archs[i].name) == 0) {
            
                cpu_arch_flags = cpu_archs[i].cpu_flags;
                
                free (cpu_arch_name);
                cpu_arch_name = xstrdup (arg);
                
                free (cpu_extensions_name);
                cpu_extensions_name = xstrdup ("");
                
                break;
            
            }
        
        }
        
        if (i == ARRAY_SIZE (cpu_archs)) {
        
            for (i = 0; i < ARRAY_SIZE (cpu_extensions); i++) {
            
                if (strcmp (arg, cpu_extensions[i].name) == 0) {
                
                    cpu_arch_flags = cpu_flags_or (cpu_arch_flags, cpu_extensions[i].cpu_flags);
                    cpu_extensions_name = xrealloc (cpu_extensions_name, strlen (cpu_extensions_name) + 1 + 1 + strlen (cpu_extensions[i].name));
                    
                    strcat (cpu_extensions_name, ".");
                    strcat (cpu_extensions_name, cpu_extensions[i].name);
                    
                    break;
                
                }
            
            }
            
            if (i == ARRAY_SIZE (cpu_extensions)) {
            
                for (i = 0; i < ARRAY_SIZE (cpu_no_extensions); i++) {
                
                    if (strcmp (arg, cpu_no_extensions[i].name) == 0) {
                    
                        cpu_arch_flags = cpu_flags_and_not (cpu_arch_flags, cpu_no_extensions[i].cpu_flags);
                        cpu_extensions_name = xrealloc (cpu_extensions_name, strlen (cpu_extensions_name) + 1 + 1 + strlen (cpu_no_extensions[i].name));
                        
                        strcat (cpu_extensions_name, ".");
                        strcat (cpu_extensions_name, cpu_no_extensions[i].name);
                        
                        break;
                    
                    }
                
                }
                
                if (i == ARRAY_SIZE (cpu_no_extensions)) {
                    report_at (get_filename (), get_line_number (), REPORT_ERROR, "invalid march option: '%s'", optarg);
                }
            
            }
        
        }
        
        if (saved_c == '\0') {
            break;
        }
        
        *next = saved_c;
        arg = next + 1;
    
    }
    
    free (orig_arg);

}

static void machine_dependent_set_bits (int new_bits, int cause_fatal_error) {

    if (new_bits == 64 && !cpu_arch_flags.cpu_long_mode) {
    
        if (cause_fatal_error) {
        
            report_at (program_name, 0, REPORT_FATAL_ERROR, "64-bit mode not supported on '%s'", cpu_arch_name ? cpu_arch_name : DEFAULT_CPU_ARCH_NAME);
            exit (EXIT_FAILURE);
        
        } else {
            report_at (get_filename (), get_line_number (), REPORT_ERROR, "64-bit mode not supported on '%s'", cpu_arch_name ? cpu_arch_name : DEFAULT_CPU_ARCH_NAME);
        }
        
        return;
    
    }
    
    if (new_bits == 32 && !cpu_arch_flags.cpu_386) {
    
        if (cause_fatal_error) {
        
            report_at (program_name, 0, REPORT_FATAL_ERROR, "32-bit mode not supported on '%s'", cpu_arch_name ? cpu_arch_name : DEFAULT_CPU_ARCH_NAME);
            exit (EXIT_FAILURE);
        
        } else {
            report_at (get_filename (), get_line_number (), REPORT_ERROR, "32-bit mode not supported on '%s'", cpu_arch_name ? cpu_arch_name : DEFAULT_CPU_ARCH_NAME);
        }
        
        return;
    
    }

    bits = new_bits;

    if (bits == 64) {
    
        cpu_arch_flags.cpu_no64 = 0;
        cpu_arch_flags.cpu_64 = 1;
    
    } else {
    
        cpu_arch_flags.cpu_no64 = 1;
        cpu_arch_flags.cpu_64 = 0;
    
    }

}


